		,HOST_


		,PORT_


		,USER_


		,PASSWORD_


		,KEY_


		,INTTOKEN_


		,TOKEN_


		,TYPE_


		,RUNTYPE_


		,USEYPE_


		,LEVEL_


		,PRIORITY_


		,OPERATION_


		,DBNAME_


		,BUCKET_


		,CATALOG_


		,INFOSERVER_


		,LOGINAS_


		,LOGINURL_


		,TICKET_


